import React from "react"
import { IMessage } from "../models/IMessage"

export interface IProps{}
export interface IState{
    readonly code: string
    readonly popup: IMessage
}

export default class Main extends React.PureComponent<IProps, IState>{
    constructor(props: IProps){
        super(props)

        this.state = {
            code: "",
            popup: {
                title: "Errore!",
                icon: "fa-times-circle",
                iconColor: "#de1e30",
                message: "Non sono riuscito ad eseguire l'accesso.",
                time: 3000
            }
        }
    }

    changeCode = (event: any) => {
        let code = event.target.value

        this.setState({
            code: code.toUpperCase()
        })

        if(code.length === 24)
            this.tryToLog()
    }

    tryToLog = () => {
        this.setState({
            popup: {
                title: "Congratulazioni!",
                message: "Accesso eseguito con successo.",
                icon: "fa-check-circle",
                iconColor: "#25ad25",
                time: 1200
            }
        })

        this.showMessagePopup()
    }

    showMessagePopup = () => {
        let popup = document.getElementById("popup")

        popup.classList.add("show")

        console.log(this.state.popup.time)
        setTimeout(() => {
            popup.classList.remove("show")
        }, this.state.popup.time)
    }

    render(): JSX.Element{
        const { popup } = this.state

        return <div className="container-fluid">

            <div className="row">
                <div className="col-3 bg-danger">

                </div>

                <div className="col-9 bg-light main-block">
                    <div className="w-100">
                        <h2 className="text-center mb-2">Scannerizza il codice</h2>
                        <input autoFocus type="text" className="form-control w-50 text-center m-auto" value={this.state.code} onChange={this.changeCode} maxLength={24} />
                    </div>

                    <div id="popup" className="col-9">
                        <div className="w-100">
                            <i className={"fal fa-fw fa-lg fa-6x " + popup.icon} style={{ color: popup.iconColor}}></i>
                            <h2 className="mt-5">{popup.title}</h2>
                            <div className="mt-2">{popup.message}</div>
                        </div>
                    </div>
                </div>


            </div>
        </div>
    }
}
