import React from "react"
import axios from "axios"
import { IMessage } from "../models/IMessage"

export interface IProps{}
export interface IState{
    readonly code: string
    readonly popup: IMessage
}

export default class Main extends React.PureComponent<IProps, IState>{
    constructor(props: IProps){
        super(props)

        this.state = {
            code: "",
            popup: {
                title: "Errore!",
                icon: "fa-times-circle",
                iconColor: "#de1e30",
                message: "Non sono riuscito ad eseguire l'accesso.",
                time: 3000
            }
        }
    }

    changeCode = (event: any) => {
        let code = event.target.value

        this.setState({
            code: code
        })

        if(code.length === 24)
            this.tryToLog(code)
    }

    tryToLog = (code: string) => {
        axios.get("http://localhost:5000/checkCode/" + code).then((response) => {
            this.setState({
                popup: response.data as IMessage
            })

            this.showMessagePopup()
        })
    }

    showMessagePopup = () => {
        let popup = document.getElementById("popup"),
        input = document.getElementById("mainInput") as HTMLInputElement

        popup.classList.add("show")
        input.setAttribute("disabled", "disabled")

        setTimeout(() => {
            popup.classList.remove("show")
            input.value = ""
            input.removeAttribute("disabled")
            input.focus()
        }, this.state.popup.time)
    }

    focusInput = () => {
        let input = document.getElementById("mainInput") as HTMLInputElement

        input.focus()
    }

    render(): JSX.Element{
        const { popup } = this.state

        return <div className="container-fluid" onClick={this.focusInput}>

            <div className="row">
                {/* <div className="col-12 col-lg-3 bg-danger">
                    
                </div> */}

                <div className="col-12 bg-light" id="mainBlock">
                    <div className="w-100 text-center">
                        <img src="https://iscrizione.fitstic.it/wp-content/uploads/2015/07/Senza-titolo-1.png" />
                        <h2 className="text-center mb-2">Scannerizza il codice</h2>
                        <input autoFocus type="password" className="form-control text-center m-auto" value={this.state.code} onChange={this.changeCode} maxLength={24} id="mainInput" />
                    </div>
                </div>

                    <div id="popup" className="col-12">
                        <div className="w-100">
                            <i className={"fal fa-fw fa-lg fa-6x " + popup.icon} style={{ color: popup.iconColor}}></i>
                            <h2 className="mt-5">{popup.title}</h2>
                            <div className="mt-2">{popup.message}</div>
                        </div>
                    </div>


            </div>
        </div>
    }
}
