import React from "react"
import axios from "axios"
import { IMessage, genericError } from "../models/IMessage"

export interface IProps{}
export interface IState{
    readonly code: string
    readonly popup: IMessage
}

export default class Main extends React.PureComponent<IProps, IState>{
    constructor(props: IProps){
        super(props)

        this.state = {
            code: "",
            popup: genericError
        }
    }

    changeCode = (event: any) => {
        let code = event.target.value

        this.setState({
            code: code
        })

        this.tryToLog(code)
    }

    tryToLog = (code: string) => {
        axios.get("http://localhost:5000/checkCode/" + code).then((response) => {
            this.setState({
                popup: response.data as IMessage
            })

            this.showMessagePopup()
        }).catch((err) => {
            this.setState({
                popup: genericError
            })

            this.showMessagePopup()
        })
    }

    showMessagePopup = () => {
        let popup = document.getElementById("popup"),
        input = document.getElementById("mainInput") as HTMLInputElement

        popup.classList.add("show")
        input.setAttribute("disabled", "disabled")

        setTimeout(() => {
            popup.classList.remove("show")
            input.value = ""
            input.removeAttribute("disabled")
            input.focus()
        }, this.state.popup.time)
    }

    focusInput = () => {
        let input = document.getElementById("mainInput") as HTMLInputElement

        input.focus()
    }

    render(): JSX.Element{
        const { popup } = this.state

        return <div className="container-fluid" onClick={this.focusInput}>

            <div className="row">
                <div className="col-12 col-lg-3 bg-info p-0 menu">
                    <div className="logo-block px-3 py-4">
                        <img src="https://iscrizione.fitstic.it/wp-content/uploads/2015/07/Senza-titolo-1.png" height="100" className="mx-auto d-block logo" alt="logo" />
                    </div>
                </div>

                <div className="col-9 bg-light" id="mainBlock">
                <iframe title="Calendario" src="https://calendar.google.com/calendar/embed?height=600&amp;wkst=2&amp;bgcolor=%23f5f5f5&amp;ctz=Europe%2FRome&amp;src=Y2toajdpcWozbXNhZTRpNGlldG01aXAxY2dAZ3JvdXAuY2FsZW5kYXIuZ29vZ2xlLmNvbQ&amp;color=%23F5F5F5&amp;showTitle=1&amp;title=Lezioni&amp;showNav=0&amp;showPrint=0&amp;showTabs=0&amp;showCalendars=0&amp;showTz=0" style={{border: "0"}} width="80%" height="80%" frameBorder="0" scrolling="no"></iframe>
                    {/* <div className="w-100 text-center">
                        <h2 className="text-center my-3">Scannerizza il codice</h2>
                        <input autoFocus type="password" className="form-control text-center m-auto" value={this.state.code} onChange={this.changeCode} maxLength={24} id="mainInput" />
                    </div> */}
                </div>

                    <div id="popup" className="col-9">
                        <div className="w-100">
                            <i className={"fal fa-fw fa-lg fa-6x " + popup.icon} style={{ color: popup.iconColor}}></i>
                            <h2 className="mt-5">{popup.title}</h2>
                            <div className="mt-2">{popup.message}</div>
                        </div>
                    </div>


            </div>
        </div>
    }
}
