import React from "react"
import { NavLink, Route, Router, Switch } from 'react-router-dom';
import { routerHistory } from ".."
import HomeComponent from "./HomeComponent";

export interface IProps{}
export interface IState{}

export default class Main extends React.Component{
    focusInput = () => {
        let input = document.getElementById("mainInput") as HTMLInputElement

        if(input)
            input.focus()
    }

    render(): JSX.Element{
        return <div className="container-fluid" onClick={this.focusInput}>


            <Router history={routerHistory}>
                <div className="row">
                    <div className="col-12 col-lg-3 bg-warning p-0 menu">
                        <div className="logo-block px-3 py-4">
                            <img src="https://iscrizione.fitstic.it/wp-content/uploads/2015/07/Senza-titolo-1.png" height="100" className="mx-auto d-block logo" alt="logo" />
                        </div>

                        <NavLink className="router-link" activeClassName="active" onClick={() => routerHistory.push("/")} exact to="/">
                            <span>Home</span>
                        </NavLink>
                        <NavLink className="router-link" activeClassName="active" onClick={() => routerHistory.push("/calendar")} exact to="/calendar">
                            <span>Lezioni</span>
                        </NavLink>
                    </div>

                    
                        <Switch>
                            <Route exact path="/" render={() => (
                                <HomeComponent />
                            )} />

                            <Route exact path="/calendar" render={() => (
                                <div className="col-9 py-4" id="mainBlock">
                                    <h3 className="mb-0">Lezioni di questo mese</h3>
                                    <iframe title="Calendario" src="https://calendar.google.com/calendar/embed?height=600&amp;wkst=2&amp;bgcolor=%23f5f5f5&amp;ctz=Europe%2FRome&amp;src=Y2toajdpcWozbXNhZTRpNGlldG01aXAxY2dAZ3JvdXAuY2FsZW5kYXIuZ29vZ2xlLmNvbQ&amp;color=%239F3501&amp;showTitle=0&amp;showDate=0&amp;showNav=0&amp;showPrint=0&amp;showTabs=0&amp;showCalendars=0&amp;showTz=0" width="80%" height="80%" frameBorder="0" scrolling="no"></iframe>
                                </div>
                            )} />
                        </Switch>
                        
                    </div>
            </Router>
        </div>
    }
}
